import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './PostDetail.css';

// 模拟数据（与 Home.js 保持一致）
const mockPosts = [
  {
    id: 1,
    user: {
      name: '小红薯用户',
      avatar: 'https://images.unsplash.com/photo-1494790108755-2616b612c03c?w=150&h=150&fit=crop&crop=face'
    },
    content: '今天的下午茶时光 ☕️ 在这家咖啡店发现了超好喝的手冲咖啡，环境也很棒！推荐给大家～',
    images: ['https://images.unsplash.com/photo-1501339847302-ac426a4a7cbb?w=400&h=400&fit=crop'],
    tags: ['咖啡', '下午茶', '生活方式'],
    likes: 128,
    comments: 23,
    timeAgo: '2小时前'
  },
  // ... 其余帖子数据 ...
];

const mockComments = [
  { id: 1, user: '热心网友', content: '看起来好棒！', time: '1小时前' },
  { id: 2, user: '美食家', content: '下次也要去试试！', time: '30分钟前' }
];

const PostDetail = () => {
  const { postId } = useParams();
  const navigate = useNavigate();
  const post = mockPosts.find(p => p.id === Number(postId));

  if (!post) return <div className="post-detail-container">未找到该帖子</div>;

  return (
    <div className="post-detail-container">
      <button className="back-btn" onClick={() => navigate(-1)}>← 返回</button>
      <div className="post-card-detail">
        <div className="user-info">
          <img className="avatar" src={post.user.avatar} alt="avatar" />
          <div>
            <div className="username">{post.user.name}</div>
            <div className="time">{post.timeAgo}</div>
          </div>
        </div>
        <div className="content">{post.content}</div>
        {post.images && post.images.length > 0 && (
          <div className="images">
            {post.images.map((img, i) => (
              <img key={i} src={img} alt="post" className="post-image" />
            ))}
          </div>
        )}
        <div className="tags">
          {post.tags.map(tag => <span key={tag} className="tag">#{tag}</span>)}
        </div>
        <div className="actions">
          <button className="like-btn">👍 {post.likes}</button>
          <button className="comment-btn">💬 {post.comments}</button>
        </div>
      </div>
      <div className="comments-section">
        <h4>评论区</h4>
        {mockComments.map(c => (
          <div key={c.id} className="comment">
            <span className="comment-user">{c.user}：</span>
            <span>{c.content}</span>
            <span className="comment-time">{c.time}</span>
          </div>
        ))}
        <div className="add-comment">
          <input type="text" placeholder="写下你的评论...（暂不支持提交）" />
          <button disabled>发送</button>
        </div>
      </div>
    </div>
  );
};

export default PostDetail;
