      }
    ],
    experienceActivities: [
      {
        id: 1,
        title: '蜡染体验课程',
        duration: '2小时',
        price: '￥128/人',
        image: 'https://images.unsplash.com/photo-1528127269322-539801943592?ixlib=rb-1.2.1&auto=format&fit=crop&w=300&q=60'
      },
