import React, { useState, useEffect, useRef } from 'react';
import { Settings, Grid, Heart, Bookmark, MapPin, Calendar, ShoppingBag, User, LogIn, UserPlus, X, Edit3, LogOut, ChevronDown } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import PostCard from '../../components/PostCard';
import './Profile.css';

// 模拟用户数据
const initialUserData = {
  id: 1,
  name: '小红薯用户',
  avatar: 'https://images.unsplash.com/photo-1494790108755-2616b612c03c?w=150&h=150&fit=crop&crop=face',
  bio: '热爱生活 | 美食探索者 | 旅行达人 ✨',
  location: '上海，中国',
  joinDate: '2023年5月',
  followers: 1234,
  following: 567,
  posts: 89,
  likes: 5678
};

// 模拟订单数据
const mockOrders = [
  {
    id: 1,
    orderNumber: 'GZ20241201001',
    type: '导游服务',
    title: '小红 - 苗族文化深度游',
    image: 'https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=150&h=150&fit=crop',
    price: '¥800',
    status: '已完成',
    date: '2024-11-28',
    location: '贵州雷山'
  },
  {
    id: 2,
    orderNumber: 'GZ20241130002',
    type: '景点门票',
    title: '黄果树瀑布门票',
    image: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=150&h=150&fit=crop',
    price: '¥180',
    status: '待使用',
    date: '2024-12-05',
    location: '贵州安顺'
  }
];

// 模拟用户的帖子
const mockUserPosts = [
  {
    id: 1,
    user: initialUserData,
    content: '今天的下午茶时光 ☕️ 在这家咖啡店发现了超好喝的手冲咖啡！',
    images: ['https://images.unsplash.com/photo-1501339847302-ac426a4a7cbb?w=400&h=400&fit=crop'],
    tags: ['咖啡', '下午茶'],
    likes: 128,
    comments: 23,
    timeAgo: '2小时前'
  },
  {
    id: 2,
    user: initialUserData,
    content: '周末自制的芝士蛋糕 🍰 第一次做居然成功了！',
    images: ['https://images.unsplash.com/photo-1578985545062-69928b1d9587?w=400&h=400&fit=crop'],
    tags: ['烘焙', '甜品'],
    likes: 256,
    comments: 45,
    timeAgo: '1天前'
  }
];

const Profile = () => {
  const [activeTab, setActiveTab] = useState('posts');
  const [isLoggedIn, setIsLoggedIn] = useState(false); // 登录状态
  const [showAuthModal, setShowAuthModal] = useState(false); // 显示登录注册模态框
  const [authMode, setAuthMode] = useState('login'); // 'login' 或 'register'
  const [showSettingsDropdown, setShowSettingsDropdown] = useState(false); // 显示设置下拉菜单
  const [showEditModal, setShowEditModal] = useState(false); // 显示编辑资料模态框
  const [userData, setUserData] = useState(initialUserData); // 用户数据状态
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    confirmPassword: '',
    email: ''
  });
  const [editFormData, setEditFormData] = useState({
    name: initialUserData.name,
    bio: initialUserData.bio,
    location: initialUserData.location,
    avatar: initialUserData.avatar
  });
  
  const dropdownRef = useRef(null);

  // 点击外部关闭下拉菜单
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowSettingsDropdown(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const tabs = [
    { id: 'posts', label: '帖子', icon: Grid, count: isLoggedIn ? mockUser.posts : 0 },
    { id: 'orders', label: '订单', icon: ShoppingBag, count: isLoggedIn ? mockOrders.length : 0 },
    { id: 'likes', label: '赞过', icon: Heart, count: isLoggedIn ? mockUser.likes : 0 },
    { id: 'saved', label: '收藏', icon: Bookmark, count: isLoggedIn ? 45 : 0 }
  ];

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (authMode === 'register' && formData.password !== formData.confirmPassword) {
      alert('两次输入的密码不一致！');
      return;
    }
    
    // 模拟登录/注册成功
    setIsLoggedIn(true);
    setShowAuthModal(false);
    setFormData({
      username: '',
      password: '',
      confirmPassword: '',
      email: ''
    });
    alert(authMode === 'login' ? '登录成功！' : '注册成功！');
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setActiveTab('posts');
    setShowSettingsDropdown(false);
  };

  const handleEditProfile = () => {
    setShowSettingsDropdown(false);
    alert('编辑资料功能开发中...');
  };

  const handleSettings = () => {
    setShowSettingsDropdown(false);
    alert('设置功能开发中...');
  };

  // 未登录状态的渲染
  if (!isLoggedIn) {
    return (
      <motion.div 
        className="profile-container"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <div className="container">
          <div className="login-prompt">
            <div className="login-card">
              <User size={64} className="login-icon" />
              <h2>欢迎来到贵州Travel</h2>
              <p>登录后可以查看个人信息、发布内容、管理订单等更多功能</p>
              
              <div className="auth-buttons">
                <button 
                  className="btn btn-primary"
                  onClick={() => {
                    setAuthMode('login');
                    setShowAuthModal(true);
                  }}
                >
                  <LogIn size={20} />
                  登录
                </button>
                <button 
                  className="btn btn-secondary"
                  onClick={() => {
                    setAuthMode('register');
                    setShowAuthModal(true);
                  }}
                >
                  <UserPlus size={20} />
                  注册
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* 登录注册模态框 */}
        <AnimatePresence>
          {showAuthModal && (
            <motion.div 
              className="modal-overlay"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={() => setShowAuthModal(false)}
            >
              <motion.div 
                className="auth-modal"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.8 }}
                onClick={(e) => e.stopPropagation()}
              >
                <div className="auth-modal-header">
                  <h3>{authMode === 'login' ? '登录' : '注册'}</h3>
                  <button 
                    className="close-btn"
                    onClick={() => setShowAuthModal(false)}
                  >
                    <X size={20} />
                  </button>
                </div>
                
                <form onSubmit={handleSubmit} className="auth-form">
                  <div className="form-group">
                    <label>用户名</label>
                    <input
                      type="text"
                      name="username"
                      value={formData.username}
                      onChange={handleInputChange}
                      required
                      placeholder="请输入用户名"
                    />
                  </div>
                  
                  {authMode === 'register' && (
                    <div className="form-group">
                      <label>邮箱</label>
                      <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleInputChange}
                        required
                        placeholder="请输入邮箱"
                      />
                    </div>
                  )}
                  
                  <div className="form-group">
                    <label>密码</label>
                    <input
                      type="password"
                      name="password"
                      value={formData.password}
                      onChange={handleInputChange}
                      required
                      placeholder="请输入密码"
                    />
                  </div>
                  
                  {authMode === 'register' && (
                    <div className="form-group">
                      <label>确认密码</label>
                      <input
                        type="password"
                        name="confirmPassword"
                        value={formData.confirmPassword}
                        onChange={handleInputChange}
                        required
                        placeholder="请再次输入密码"
                      />
                    </div>
                  )}
                  
                  <button type="submit" className="btn btn-primary btn-full">
                    {authMode === 'login' ? '登录' : '注册'}
                  </button>
                </form>
                
                <div className="auth-switch">
                  {authMode === 'login' ? (
                    <p>
                      还没有账号？
                      <button 
                        type="button"
                        className="text-btn"
                        onClick={() => setAuthMode('register')}
                      >
                        立即注册
                      </button>
                    </p>
                  ) : (
                    <p>
                      已有账号？
                      <button 
                        type="button"
                        className="text-btn"
                        onClick={() => setAuthMode('login')}
                      >
                        立即登录
                      </button>
                    </p>
                  )}
                </div>
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>
      </motion.div>
    );
  }

  // 已登录状态的渲染
  return (
    <motion.div 
      className="profile-container"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <div className="container">
        {/* 个人资料头部 */}
        <div className="profile-header">
          <div className="profile-info">
            <div className="avatar-section">
              <img 
                src={mockUser.avatar} 
                alt={mockUser.name}
                className="profile-avatar"
              />
              <button className="edit-avatar-btn">
                <Settings size={16} />
              </button>
            </div>
            
            <div className="user-details">
              <h1 className="user-name">{mockUser.name}</h1>
              <p className="user-bio">{mockUser.bio}</p>
              
              <div className="user-meta">
                <div className="meta-item">
                  <MapPin size={14} />
                  <span>{mockUser.location}</span>
                </div>
                <div className="meta-item">
                  <Calendar size={14} />
                  <span>加入于 {mockUser.joinDate}</span>
                </div>
              </div>
            </div>
          </div>

          {/* 统计数据 */}
          <div className="stats-section">
            <div className="stat-item">
              <span className="stat-number">{mockUser.posts}</span>
              <span className="stat-label">帖子</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">{mockUser.followers}</span>
              <span className="stat-label">粉丝</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">{mockUser.following}</span>
              <span className="stat-label">关注</span>
            </div>
          </div>

          {/* 操作按钮 */}
          <div className="profile-actions">
            <div className="settings-dropdown" ref={dropdownRef}>
              <button 
                className="settings-btn"
                onClick={() => setShowSettingsDropdown(!showSettingsDropdown)}
              >
                <Settings size={20} />
                <ChevronDown size={16} className={`chevron ${showSettingsDropdown ? 'rotated' : ''}`} />
              </button>
              
              <AnimatePresence>
                {showSettingsDropdown && (
                  <motion.div 
                    className="dropdown-menu"
                    initial={{ opacity: 0, y: -10, scale: 0.95 }}
                    animate={{ opacity: 1, y: 0, scale: 1 }}
                    exit={{ opacity: 0, y: -10, scale: 0.95 }}
                    transition={{ duration: 0.2 }}
                  >
                    <button className="dropdown-item" onClick={handleEditProfile}>
                      <Edit3 size={16} />
                      <span>编辑资料</span>
                    </button>
                    <button className="dropdown-item" onClick={handleSettings}>
                      <Settings size={16} />
                      <span>设置</span>
                    </button>
                    <div className="dropdown-divider"></div>
                    <button className="dropdown-item logout" onClick={handleLogout}>
                      <LogOut size={16} />
                      <span>退出登录</span>
                    </button>
                  </motion.div>
                )}
              </AnimatePresence>
            </div>
          </div>
        </div>

        {/* 标签页导航 */}
        <div className="profile-tabs">
          {tabs.map((tab) => {
            const IconComponent = tab.icon;
            return (
              <button
                key={tab.id}
                className={`tab-btn ${activeTab === tab.id ? 'active' : ''}`}
                onClick={() => setActiveTab(tab.id)}
              >
                <IconComponent size={20} />
                <span>{tab.label}</span>
                <span className="tab-count">({tab.count})</span>
              </button>
            );
          })}
        </div>

        {/* 内容区域 */}
        <div className="profile-content">
          {activeTab === 'posts' && (
            <div className="posts-grid">
              {mockUserPosts.map((post, index) => (
                <motion.div
                  key={post.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.3, delay: index * 0.1 }}
                >
                  <PostCard post={post} />
                </motion.div>
              ))}
            </div>
          )}

          {activeTab === 'orders' && (
            <div className="orders-grid">
              {mockOrders.map((order, index) => (
                <motion.div
                  key={order.id}
                  className="order-card"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.3, delay: index * 0.1 }}
                >
                  <div className="order-header">
                    <div className="order-number">订单号：{order.orderNumber}</div>
                    <div className={`order-status ${order.status === '已完成' ? 'completed' : 'pending'}`}>
                      {order.status}
                    </div>
                  </div>
                  
                  <div className="order-content">
                    <img src={order.image} alt={order.title} className="order-image" />
                    <div className="order-details">
                      <div className="order-type">{order.type}</div>
                      <h4 className="order-title">{order.title}</h4>
                      <div className="order-location">
                        <MapPin size={14} />
                        <span>{order.location}</span>
                      </div>
                      <div className="order-date">使用日期：{order.date}</div>
                    </div>
                    <div className="order-price">{order.price}</div>
                  </div>
                  
                  <div className="order-actions">
                    <button className="btn btn-small btn-outline">查看详情</button>
                    {order.status === '待使用' && (
                      <button className="btn btn-small btn-primary">立即使用</button>
                    )}
                  </div>
                </motion.div>
              ))}
            </div>
          )}

          {activeTab === 'likes' && (
            <div className="empty-state">
              <Heart size={64} className="empty-icon" />
              <h3>还没有赞过的内容</h3>
              <p>去发现一些精彩的内容吧！</p>
            </div>
          )}

          {activeTab === 'saved' && (
            <div className="empty-state">
              <Bookmark size={64} className="empty-icon" />
              <h3>还没有收藏的内容</h3>
              <p>收藏你喜欢的帖子，随时回来查看！</p>
            </div>
          )}
        </div>
      </div>
    </motion.div>
  );
};

export default Profile;
